<script lang="ts">
import { Ref } from "vue";
export default {
	setup() {
		let typeValue: Ref<string | number> = ref("");
		let typeStatus: Ref<boolean> = ref(false);
		const displayTextArray: string[] = [
			"UI/UX Engineer",
			"Frontend Engineer",
			"Backend Engineer",
		];
		const typingSpeed: number = 50;
		const erasingSpeed: number = 50;
		const newTextDelay: number = 500;
		let displayTextArrayIndex: number = 0;
		let charIndex: number = 0;

		const eraseText = () => {
			if (charIndex > 0) {
				if (!typeStatus.value) typeStatus.value = true;
				typeValue.value = displayTextArray[displayTextArrayIndex].substring(
					0,
					charIndex - 1
				);
				charIndex -= 1;
				setTimeout(eraseText, erasingSpeed);
			} else {
				typeStatus.value = false;
				displayTextArrayIndex += 1;
				if (displayTextArrayIndex >= displayTextArray.length)
					displayTextArrayIndex = 0;
				setTimeout(typeText, typingSpeed + 1000);
			}
		};

		const typeText = () => {
			if (charIndex < displayTextArray[displayTextArrayIndex].length) {
				if (!typeStatus.value) typeStatus.value = true;
				typeValue.value +=
					displayTextArray[displayTextArrayIndex].charAt(charIndex);
				charIndex += 1;
				setTimeout(typeText, typingSpeed);
			} else {
				typeStatus.value = false;
				setTimeout(eraseText, newTextDelay);
			}
		};

		onMounted(() => {
			setTimeout(typeText, newTextDelay + 200);
		});

		return {
			typeValue,
			typeStatus,
		};
	},
};
</script>

